mapper.select:
select d.id,d.name,d.type,d.pic,d.recommend,d.price,d.introduction from shop s join shop_menu sm on sm.shop_id = s.id join dishes d on d.type=sm.menu_id where s.id = #{shop_id} and 1=1 and enable=1
and d.name like '%' #{conditiondishes} '%'
limit #{startrows},#{endrows}

select d.id,d.name,d.type,d.pic,d.recommend,d.price,d.introduction from shop s join shop_menu sm on sm.shop_id = s.id join dishes d on d.type=sm.menu_id where s.id = #{shop_id} and 1=1 and enable=1
and d.type like '%' #{conditiondishes} '%'
limit #{startrows},#{endrows}
